-- ----------------------------
-- Table structure for blog
-- ----------------------------
DROP TABLE IF EXISTS `blog`;
CREATE TABLE `blog`  (
  `b_id` int(0) NOT NULL AUTO_INCREMENT COMMENT '博客ID',
  `b_title` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '标题',
  `b_abstract` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT '摘要',
  `b_picture` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '首图',
  `b_content` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT '内容',
  `b_date` date NULL DEFAULT NULL COMMENT '发表日期',
  `b_popularity` int(0) NULL DEFAULT NULL COMMENT '人气数',
  `b_comment` int(0) NULL DEFAULT NULL COMMENT '评论数',
  `b_fabulous` int(0) NULL DEFAULT NULL COMMENT '点赞数',
  `b_sign` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '标记（原创/转载）',
  `b_release` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '发布（保存/发布）',
  `u_id` int(0) NULL DEFAULT NULL COMMENT '作者id',
  `ty_id` int(0) NULL DEFAULT NULL COMMENT '类型id',
  `ta_id_string` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '标签id串',
  PRIMARY KEY (`b_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 58 CHARACTER SET = utf8 COLLATE = utf8_bin ROW_FORMAT = Dynamic;

