  `c_email` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '邮箱',
  `c_content` text CHARACTER SET utf8 COLLATE utf8_bin NULL COMMENT '评论内容',
  `c_date` date NULL DEFAULT NULL COMMENT '评论时间',
  `c_type` varchar(20) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '类型（@  /  点赞  /   评论   /   私信）',
  `b_id` int(0) NULL DEFAULT NULL COMMENT '博客id',
  `relation_id` int(0) NULL DEFAULT NULL COMMENT '自关联id',
  `c_picture` varchar(255) CHARACTER SET utf8 COLLATE utf8_bin NULL DEFAULT NULL COMMENT '头像',
  PRIMARY KEY (`c_id`) USING BTREE
